x-code-samples.source:
sdk.workflows.update({ buildId }, {
    pullRequest: {
        createOnSuccess: CREATE_ON_SUCCESS_BOOL,
        mergeOnSuccess: MERGE_ON_SUCCESS_BOOL
    }
})

sdk.workflows.buildByService({ serviceId }, {
    branch: `${BRANCH_STR}`,
    buildFlags: {
        nocache: NOCACHE_BOOL
    },
    sha: `${SHA_STR}`
})

sdk.compositions.create({
    isAdvanced: IS_ADVANCED_BOOL,
    name: `${NAME_STR}`,
    vars: [
        { ...VARS_OBJ }
    ],
    yamlJson: `${YAML_JSON_STR}`
})

sdk.compositions.update({ id }, {
    isAdvanced: IS_ADVANCED_BOOL,
    name: `${NAME_STR}`,
    vars: [
        { ...VARS_OBJ }
    ],
    yamlJson: `${YAML_JSON_STR}`
})

sdk.images.upsertMetadata({ dockerImageId }, {
    CF_QUALITY: CF_QUALITY_BOOL,
    comment: `${COMMENT_STR}`,
    coverage: `${COVERAGE_STR}`,
    failed_tests: FAILED_TESTS_NUM
})

sdk.payments.create({
    couponId: `${COUPON_ID_STR}`,
    plan: {
        builds: BUILDS_NUM,
        environments: ENVIRONMENTS_NUM,
        id: `${ID_STR}`
    },
    token: { ...TOKEN_OBJ }
})

sdk.runtime.launch({
    branch: `${BRANCH_STR}`,
    repoData: {
        url: {
            https: `${HTTPS_STR}`
        }
    },
    repoName: `${REPO_NAME_STR}`,
    repoOwner: `${REPO_OWNER_STR}`,
    serviceId: `${SERVICE_ID_STR}`,
    sha: `${SHA_STR}`
})

sdk.repos.create({ context }, {
    serviceDetails: {
        name: `${NAME_STR}`,
        scm: {
            name: `${NAME_STR}`,
            owner: {
                name: `${NAME_STR}`
            }
        }
    }
})
